package Mock3;

public class Programm28 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for(int c=0; c<5; c++)
		{
			
			for(int a = 0; a<5 - c; a++)
			{
				System.out.print(" ");
			}
			
			for(int b=0; b<=c; b++)
			{
				System.out.print("* ");
			}

			System.out.println();
			
		}
		
		

		
		
	}

}
