package Mock3;

public class Programm30 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int b=0; b<5; b++)
		{
			for(int a=0; a<=b; a++)
			{
				System.out.print("♥ ");
			}
			
			System.out.println();
		}
		
		
		for(int b=3; b>=0; b--)
		{
			for(int a=0; a<=b; a++)
			{
				System.out.print("♥ ");
			}
			
			System.out.println();
		}
		
		
	}

}
